import './game.css'
import { useNavigate } from 'react-router-dom'

function Game() {
  let navigate = useNavigate()

  const handleNormalClick = () => {
    navigate('/game/normal')
    // navigate('/game/normal', {
    //   state: {
    //     answerWord: normalWords[Math.floor(Math.random() * normalWords.length)],
    //   },
    // })
  }

  const handleHardClick = () => {
    navigate('/game/hard')
    // , {
    //   state: {
    //     answerWord: hardWords[Math.floor(Math.random() * hardWords.length)],
    //   },
    // })
  }

  // Got these words from ChatGPT
  const normalWords = [
    'NUMBER',
    'PEOPLE',
    'BEFORE',
    'LETTER',
    'LITTLE',
    'YELLOW',
    'WINDOW',
    'BOTTLE',
    'BUTTON',
    'BATTLE',
  ]
  const hardWords = [
    'abalone',
    'bandana',
    'chicken',
    'dolphin',
    'eclipse',
    'furnace',
    'gazelle',
    'haddock',
    'inquiry',
    'javelin',
  ]

  return (
    <div className="Game">
      <div className="game-box">
        <h1 className="difficulty">Game Mode Select</h1>
        <div className="difficult-mode">
          <button className="game-btn" onClick={handleNormalClick}>
            Normal
          </button>
          <button className="game-btn" onClick={handleHardClick}>
            Hard
          </button>
        </div>
      </div>
    </div>
  )
}

export default Game
