// export default Grid
import React, { useState } from 'react'

function Grid(props) {
  // State to hold the grid data
  const [gridData, setGridData] = useState([])

  // Function to handle changes in the grid data
  function handleGridChange(event, row, col) {
    const newData = [...gridData]
    newData[row][col] = event.target.value
    setGridData(newData)
  }

  // Render the grid
  return (
    <div className="grid">
      {gridData.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <input
              key={colIndex}
              type="text"
              maxLength={1}
              value={cell}
              onChange={(event) => handleGridChange(event, rowIndex, colIndex)}
              style={{
                border: '1px solid black',
                width: '30px',
                height: '30px',
                textAlign: 'center',
              }}
            />
          ))}
        </div>
      ))}
    </div>
  )
}

export default Grid
