import React, { useState, useEffect } from 'react'
import Grid from './grid'
import GridContext from './gridContext'
// import { useLocation } from 'react-router-dom'

function GenerateAnswer(mode) {
  return mode === 'normal'
    ? normalWords[Math.floor(Math.random() * normalWords.length)]
    : hardWords[Math.floor(Math.random() * hardWords.length)]
}

function GameDetail(props) {
  const [gridData, setGridData] = useState([])
  const [row, setRow] = useState(0)
  const [col, setCol] = useState(0)
  const [answer, setAnswer] = useState(GetAnswer(props.mode))

  const updateGridData = (newData) => {
    setGridData(newData)
  }
  // Got these words from ChatGPT
  const normalWords = [
    'NUMBER',
    'PEOPLE',
    'BEFORE',
    'LETTER',
    'LITTLE',
    'YELLOW',
    'WINDOW',
    'BOTTLE',
    'BUTTON',
    'BATTLE',
  ]
  const hardWords = [
    'abalone',
    'bandana',
    'chicken',
    'dolphin',
    'eclipse',
    'furnace',
    'gazelle',
    'haddock',
    'inquiry',
    'javelin',
  ]
  // useEffect(() => {
  //   // Your code here
  //   answerWord =
  //     props.mode === 'normal'
  //       ? normalWords[Math.floor(Math.random() * normalWords.length)]
  //       : hardWords[Math.floor(Math.random() * hardWords.length)]
  // }, [])

  // const answerWord = useLocation().state.answerWord

  return (
    <GridContext.Provider value={{ gridData, updateGridData }}>
      <Grid
        rows={props.row}
        cols={props.col}
        row={row}
        setRow={setRow}
        col={col}
        setCol={setCol}
        attempt={props.row}
        answerWord={answer}
      />
    </GridContext.Provider>
  )
}

export default GameDetail
