  // Render the grid
  return (
    <div className="grid">
      {gridData.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <input
              key={colIndex}
              type="text"
              maxLength={1}
              value={cell}
              //   onChange={(event) => handleGridChange(event, rowIndex, colIndex)}
              style={{
                border: '1px solid black',
                width: '30px',
                height: '30px',
                textAlign: 'center',
              }}
              //   onKeyDown={(event) => {
              //     if (event.key === 'ArrowLeft' && colIndex > 0) {
              //       inputRefs.current[rowIndex][colIndex - 1].focus()
              //     } else if (
              //       event.key === 'ArrowRight' &&
