import React, { useContext, useState } from 'react'
import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
import Gamekeyboard from './Keyboard/Gamekeyboard'
import './grid.css'
import WordleContext from './WordleContext'

function Grid(props) {
  const {
    gridDataObject,
    rows,
    cols,
    rowObject,
    colObject,
    answerObject,
    mode,
  } = useContext(WordleContext)

  const [gridData, setGridData] = useState(
    createGridData(props.rows, props.cols),
  )
  //id:rowIndex*props.cols+colIndex value: 0,1,2
  const [boxStatus, setBoxStatus] = useState([])
  const [attempt, setAttempt] = useState(props.rows)
  const answer = props.answerWord
  console.log('answer + ' + answer)

  function handleGridChange(newWord) {
    setGridData(newWord)
  }

  function handleButton(e) {
    let curWord = [...gridData]

    switch (e) {
      case 'Enter': // CR
        console.log(props.col + ' ' + props.row + ' ' + curWord)
        if (props.col === props.cols) {
          if (checkLetters(curWord, props.row, answer)) {
            // congraduate users and exit the program
            setTimeout(() => {
              const tryAgain = window.confirm(
                'Congratulations!  Would you like to try again?',
              )
              if (tryAgain) {
                // reset the program
                resetGame()
              } else {
                // exit the program
                window.stop()
              }
            }, 80)
          } else {
            // deduct user's attempt
            // if there are remaining attempts, reset the current row and col cursor
            if (attempt !== 1) {
              props.setRow((row) => row + 1)
              props.setCol((col) => 0)
              setAttempt((att) => att - 1)
            } else {
              // exit the program or reset the program
              window.stop()
            }
          }

          return
        } else {
          // prompt that user "not enough letters"
          toast.warning('not enough letters', {
            position: toast.POSITION.TOP_RIGHT,
          })
        }
        return
      case 'Backspace':
        if (curWord.length > 0) {
          //delete the last character in current row
          curWord[props.row][props.col - 1] = ''
          props.setCol(props.col - 1)
        }
        break
      default:
        if (props.col === props.cols) {
          return
        }
        curWord[props.row][props.col] += e
        props.setCol((col) => col + 1)
        break
    }
    handleGridChange(curWord)
  }

  function resetGame() {
    // reset the program
    setGridData(createGridData(props.rows, props.cols))
    setBoxStatus((prevBoxStatus) => [])
    setAttempt((att) => props.rows)
    props.setRow((row) => 0)
    props.setCol((col) => 0)
    props.setAnswer(GenerateAnswer([props.mode]))
  }

  function checkLetters(curWord, row, answer) {
    const map = new Map()
    //items in the set have been compared
    const set = new Set()
    let flag = true
    let arrOfBox = []
    addItemInMap(map, answer)

    for (let i = 0; i < curWord.length; i++) {
      if (curWord[row][i] === answer[i]) {
        if (map.get(answer[i]) > 0) {
          //key: rowIndex*props.cols+colIndex,  location and value correct
          arrOfBox.push({ id: row * props.cols + i, value: '2' })
          map.set(answer[i], map.get(answer[i]) - 1)
          set.add(i)
        }
      } else {
        flag = false
      }
    }
    for (let j = 0; j < curWord.length; j++) {
      if (set.has(j)) continue
      for (let k = 0; k < answer.length; k++) {
        //values are same but location are different
        if (
          !set.has(j) &&
          curWord[row][j] === answer[k] &&
          map.get(answer[k]) > 0
        ) {
          arrOfBox.push({ id: row * props.cols + j, value: '1' })
          map.set(answer[k], map.get(answer[k]) - 1)
          set.add(j)
        }
      }
    }
    setBoxStatus((prevBoxStatus) => [...prevBoxStatus, ...arrOfBox])

    return flag
  }

  function updateColorOfBox(rowIndex, cols, colIndex) {
    let status = 'none'
    boxStatus.map((item) => {
      if (item.id === rowIndex * cols + colIndex) {
        status = item.value === '1' ? 'yellow' : 'green'
      }
    })
    return status
  }

  // Render the grid
  return (
    <div className="game-page">
      <h1 className="title"> Wordle</h1>
      <div className="grid">
        {gridData.map((row, rowIndex) => (
          <div key={rowIndex} className="row">
            {row.map((cell, colIndex) => (
              <div
                className="rowItem"
                key={colIndex}
                data-state={updateColorOfBox(rowIndex, props.cols, colIndex)}
              >
                {gridData[rowIndex][colIndex]}
              </div>
            ))}
          </div>
        ))}
      </div>
      <br />
      <div className="my-game-keyboard">
        <Gamekeyboard
          lines={gridData[props.row][props.col]}
          answer={answer}
          onClick={(e) => handleButton(e)}
        />
      </div>
      <button className="reset-btn" onClick={() => resetGame()}>
        RESET GAME
      </button>
      <ToastContainer />
    </div>
  )
}

function createGridData(rows, cols) {
  // Initialize grid data to empty strings
  const data = Array(rows)
    .fill()
    .map(() => Array(cols).fill(''))
  return data
}

function addItemInMap(map, answer) {
  for (let i = 0; i < answer.length; i++) {
    const key = answer[i]
    if (map.has(key)) {
      map.set(key, map.get(key) + 1)
    } else {
      map.set(key, 1)
    }
  }
}

export default Grid
