import React, { useState } from 'react'
import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
import Gamekeyboard from './Keyboard/Gamekeyboard'

function Grid(props) {
  // State to hold the grid data
  //   // Array to store refs to each input element
  //   const inputRefs = useRef(
  //     Array(props.rows)
  //       .fill()
  //       .map(() => Array(props.cols).fill(null)),
  //   )

  //   // Function to handle changes in the grid data
  // function handleGridChange(event, row, col) {
  //   const newData = [...gridData]
  //   newData[row][col] = event.target.value
  //   setGridData(newData)

  //   // Move focus to next input element
  //   if (col < props.cols - 1) {
  //     inputRefs.current[row][col + 1].focus()
  //   }
  // }
  //   // Function to handle Enter key press
  //   function handleEnterKeyPress(event, row, col) {
  //     // Move focus to next row
  //     if (row < props.rows - 1) {
  //       inputRefs.current[row + 1][0].focus()
  //     }
  //   }
  const [gridData, setGridData] = useState(
    createGridData(props.rows, props.cols),
  )

  function handleGridChange(newWord) {
    setGridData(newWord)
  }

  function handleButton(e) {
    // if (this.state.win) return
    //   let currentWord = ""
    // if (this.state.theCurrentLineNo === 6) {
    //   return
    // }

    let curWord = [...gridData]
    console.log('fffff  ' + curWord)

    // console.log(
    //   e +
    //     ' ' +
    //     gridData +
    //     ' ' +
    //     curWord +
    //     ' ' +
    //     props.rows +
    //     ' ' +
    //     props.cols +
    //     ' ' +
    //     props.row +
    //     ' ' +
    //     props.col,
    // )

    switch (e) {
      case 'Enter': // CR
        if (props.col === props.cols) {
          if (checkLetters(curWord, props.row, 'abbotw')) {
            // congraduate users and exit the program
            const tryAgain = window.confirm(
              'Congratulations!  Would you like to try again?',
            )
            if (tryAgain) {
              // reset the program
            } else {
              // exit the program
              window.stop()
            }
          } else {
            // deduct user's attempt
            // if there are remaining attempts, reset the current row and col cursor
            if (props.attempt !== 0) {
              props.setRow((row) => row + 1)
              props.setCol((col) => 0)
            } else {
              // exit the program or reset the program
              window.stop()
            }
          }

          return
        } else {
          // prompt that user "not enough letters"
          toast.warning('not enough letters', {
            position: toast.POSITION.TOP_RIGHT,
          })
        }
        return
      case 'Backspace': //bS
        if (curWord.length > 0) {
          // let newWord = curWord.toString();
          //  newWord = new
          curWord = curWord[props.row].splice(-1, 1)
          //(0, curWord.length - 1)
        }
        break
      default:
        if (props.col === props.cols) {
          return
        }
        curWord[props.row][props.col] += e
        props.setCol((col) => col + 1)
        break
    }
    handleGridChange(curWord)
  }

  // Render the grid
  return (
    <div className="grid">
      {gridData.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <div
              key={colIndex}
              //   onChange={(e) => handleGridChange(e)}
              style={{
                border: '1px solid black',
                width: '30px',
                height: '30px',
                textAlign: 'center',
              }}
              //   onKeyDown={(event) => {
              //     if (event.key === 'ArrowLeft' && colIndex > 0) {
              //       inputRefs.current[rowIndex][colIndex - 1].focus()
              //     } else if (
              //       event.key === 'ArrowRight' &&
              //       colIndex < props.cols - 1
              //     ) {
              //       inputRefs.current[rowIndex][colIndex + 1].focus()
              //     } else if (event.key === 'Enter') {
              //       handleEnterKeyPress(event, rowIndex, colIndex)
              //     }
              //   }}
              //   ref={(input) => {
              //     inputRefs.current[rowIndex][colIndex] = input
              //   }}
            >
              {gridData[rowIndex][colIndex]}
            </div>
          ))}
        </div>
      ))}
      <br />
      <Gamekeyboard
        lines={gridData}
        answer={'abbotw'}
        onClick={(e) => handleButton(e)}
      />
      <ToastContainer />
    </div>
  )
}

function createGridData(rows, cols) {
  // Initialize grid data to empty strings
  const data = Array(rows)
    .fill()
    .map(() => Array(cols).fill(''))
  return data
}

function checkLetters(curWord, row, answer) {
  for (let i = 0; i < curWord.length; i++) {
    if (curWord[row][i] !== answer[i]) return false
  }
  return true
}

export default Grid
